import React from 'react';
import * as d3 from 'd3';

export const useD3 = (renderChartFn) => {
    const ref = React.useRef();

    React.useEffect(() => {
        renderChartFn(d3.select(ref.current));//回调，包含要执行的D3.js
        return () => {};
      });//固定长度数组，告诉React何时运行renderChartFn
    return ref;
}
