import React, { useState, useEffect } from "react"
import * as d3 from "d3"

function UniTipBar(props) {
    const ref = React.useRef(null)
    const [d, setD] = useState(null)

    useEffect(() => {
        console.log(props.uniData)
        for (var i = 0; i < props.data5.length; i++) {
            if (props.data5[i].properties.name == props.searchInput) {
                setD(props.data5[i].properties)
            }
        }

        const svg = d3.select(ref.current);
        svg.selectAll("*").remove();
        // svg


    }, [props.searchInput, props.uniData])

    return (
        <div>
            {props.callUniTip &&

                <div class='universityChart'>
                    <svg viewBox="0 0 960 600" ref={ref} ></svg>
                </div>
            }
            {props.callUniTip &&

                <div class='universityTip'>
                    <p>
                        <span className='name'>{d.name} </span>
                        <br />
                        <span className="count">{props.searchData.length}</span><span className="huge"> {props.selectSerious && "HUGE"}</span><span> CASES in {props.selectLowerRange && "200km"}{!props.selectLowerRange && "500km"} </span>
                    </p>
                </div>
            }
        </div>
    )


}

export default UniTipBar
