import React, { useState, useEffect } from "react"
import { geoEqualEarth, geoPath } from "d3-geo"
import { useD3 } from './hooks/useD3';
import * as d3 from "d3"
import { feature } from "topojson-client"

const projection = geoEqualEarth()
  .scale(160)
  .translate([ 800 / 2, 450 / 2 ])


function Map(data){
  const [statesData, setStatesData] = useState([]);

  // const [statesTest, setStatesTest] = useState([]);

  useEffect(() => {
    (async () => {
      const res = await fetch('./us.json')
      const statesData = await res.json();
      setStatesData(statesData);
    })();
  }, []);

  // console.log(data)

  const ref = useD3(
    (svg) => {
        svg
        .attr("class", "USA")
        .selectAll("path")
        .data(statesData)
        .enter()
        .append("path")
        .attr("d", statesData.shape)


        // .style("opacity", function(d) { return '0.8'; })

      },[statesData.length]
  )

  return (
     <svg viewBox="0 0 960 600" className="usMap" ref={ref}>
      {/* {statesData.map((stateData, index) =>
        <path
          className="state"
          id={stateData.id}
          style={{cursor: "pointer"}}
          key={index}
          strokeWidth="0.5px"
          d={stateData.shape} 
        >
        </path>
      )} */}
    </svg>
  )
}

export default Map
