     <svg viewBox="0 0 960 600" className="usMap">
      {statesData.map((stateData, index) =>
        <path
          className="state"
          id={stateData.id}
          style={{cursor: "pointer", opacity: statesTest[index].num}}
          key={index}
          // stroke="#fff"
