import React, { useState, useEffect } from "react"
// import { geoEqualEarth, geoPath } from "d3-geo"
import d3Tip from "d3-tip"
import { geoAlbersUsa } from "d3-geo"
import * as d3 from "d3"

function MapWithCity(props) {

  const ref = React.useRef(null);
  const [statesData, setStatesData] = useState([]);

  useEffect(() => {
    (async () => {

      const res = await fetch('./gz_2010_us_050_00_500k.json')
      const statesDataCombine = await res.json();
      const statesData = statesDataCombine.features
      setStatesData(statesData);

      const projection = geoAlbersUsa()
      .scale(props.scaleSend)
      .translate([960 / 2, 600 / 2])

      const chartEl = d3.select(ref.current);
      chartEl.selectAll("*").remove();

      const chart = chartEl.append("g")

      const path = d3.geoPath()
        .projection(projection);

      chart.selectAll("path")
        .data(statesData)
        .enter()
        .append("path")
        .attr("class", "state")
        .attr("id", function (d) {
          return d.properties.NAME
        })
        .attr("d", path)
        .style("fill", function (d) {return rgba(0.0,0.0,0.0,0.0)})
        .style(
          "stroke", function (d) {
            return "494949"
          }
        )
        .style(
          "stroke-width", function (d) {
            // if (props.selectVote) {
              return 0.01
            // }
          }
        )

    })();

  }, [props.scaleSend, props.selectCases, props.selectPolicy, props.selectVote, props.selectGunRate,props.selectCity,props.selectUni,props.searchData]
  )


  return (

      <svg viewBox="0 0 960 600" className="usMap Map MapCity" ref={ref}>
      </svg>

  )
}

export default MapWithCity
