import React from 'react';
import Map from './Map';
import Mouse from './Mouse'
import Title from './Title'
import ControlBar from './ControlBar'
import SubPage from './SubPage'
import * as d3 from "d3"
// import DataProcess from './MapCharts';

function App() {
  const [data1, setData1] = React.useState([]);
  const [data2, setData2] = React.useState([]);
  const [data3, setData3] = React.useState([]);
  const [data4, setData4] = React.useState([]);
  const [select, setSelect] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [scaleData, setScaleData] = React.useState(900);
  const [pace, setPace] = React.useState(12);
  const [offset, setOffset] = React.useState([0,0]);

  React.useEffect(() => {
    (async () => {
      const res1 = await fetch('./policy.json')
      const data1 = await res1.json();
      setData1(data1);
      console.log(data1.total);

      const res2 = await fetch('./policy.json')
      const data2 = await res2.json();
      setData2(data2);

      const res3 = await fetch('./cases.geojson')
      const data3Combine = await res3.json();
      const data3 = data3Combine.features;
      setData3(data3);

      // const res4 = await fetch('./colorTest4.json')
      // const data4 = await res4.json();
      // setData4(data4);

      setSelect([1, 1, 1, 1])

      setLoading(false);

      setScaleData(900);

      setPace(12);

      setOffset([0,0])
    })();
  }, []);

  const scaleBig = event => {

    // let scale = 900+(event.target.value-50)*15;
    setScaleData(1200);
    setPace(2.0);
    console.log(scaleData)
    setOffset([150,250])

  }

  const scaleSmall = event => {

    // let scale = 900+(event.target.value-50)*15;
    setScaleData(600);
    setPace(15.0);
    console.log(scaleData)
    setOffset([0,0])

  }

  const scaleOrigin = event => {

    // let scale = 900;
    setScaleData(900);
    setPace(12.0);
    console.log(scaleData)
    setOffset([0,0])

  }

  const selectChangeGunRate = () => {
    if (select[0] == 0) {
      select[0] = 1
      setSelect(select)
      // console.log(select)
    } else {
      select[0] = 0
      setSelect(select)
      // console.log(select)
    }
  }

  const selectChangePolicy = () => {
    if (select[1] == 0) {
      select[1] = 1
      setSelect(select)
      // console.log(select)
    } else {
      select[1] = 0
      setSelect(select)
      // console.log(select)
    }
  }

  const selectChangeCases = () => {
    if (select[2] == 0) {
      select[2] = 1
      setSelect(select)
      // console.log(select)
    } else {
      select[2] = 0
      setSelect(select)
      // console.log(select)
    }
  }

  const selectChangeVote = () => {
    if (select[3] == 0) {
      select[3] = 1
      setSelect(select)
      // console.log(select)
    } else {
      select[3] = 0
      setSelect(select)
      // console.log(select)
    }
  }

  console.log(select)
  console.log(select==[1,0,1,0])

  return (
    <div className="App">
      <header className="App-header">
        <Title />
        <div className="HideOverFlow">
        <Mouse>
          {({ x, y }) => (
            <div className="Map-container">
              <div
                style={{
                  position: 'absolute',
                  top: -y / pace + offset[0],
                  left: -x / pace + offset[1]
                }}>
                {loading && <div>loading</div>}
                {!loading && <Map
                  data1={data1}
                  data2={data2}
                  data3={data3}
                  // data4={data4}
                  scaleSend={scaleData}
                  selectSend={select}
                  className="Map"  
                />}
              </div>
            </div>
          )}
        </Mouse>
        </div>
        <ControlBar
        selectSend={select}
        clickEventsCases={selectChangeCases}
        clickEventsPolicy={selectChangePolicy}
        clickEventsGunRate={selectChangeGunRate}
        clickEventsVote={selectChangeVote}
        // scaleChange={scaleChange}
        scaleBig={scaleBig}
        scaleSmall={scaleSmall}
        scaleOrigin={scaleOrigin}

         />

      {select[0] && <SubPage />}
      {!select[0] && <div>111</div>}


      </header>
    </div>
  );
}

export default App;
