import React, { useState, useEffect } from "react"

function UniTipBar(props) {

    const [d, setD] = useState(null)

    useEffect(() => {

        for (var i = 0; i < props.data5.length; i++) {
            if (props.data5[i].properties.name == props.searchInput) {
                setD(props.data5[i].properties)
            }
        }
    }, [props.searchInput])

    return (
        <div>
            {props.callUniTip &&
                <div class='universityTip'>
                    <p>
                        <span className="count">{props.searchData.length}</span><span> {props.selectSerious && "HUGE"} CASES in {props.selectLowerRange && "200km"}{!props.selectLowerRange && "500km"} </span>
                        <br/>
                        <span className='name'>{d.name} </span>
                    </p>
                </div>
            }
        </div>
    )


}

export default UniTipBar
