import React, { useState, useEffect } from "react"
import * as d3 from "d3"

function Profile(props) {
  const [statesData, setStatesData] = useState([]);     //react用来注释数据
  const ref = React.useRef(null)
  // console.log(data)

  useEffect(() => {
    (async () => {
      // set the dimensions and margins of the graph
      const margin = { top: 10, right: 30, bottom: 30, left: 60 },
        width = 760 - margin.left - margin.right,
        height = 400 - margin.top - margin.bottom;

      // append the svg object to the body of the page
      const svg = d3.select(".usProfile")
        .append("svg")
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)
        .append("g")
        .attr("transform",
          `translate(${margin.left}, ${margin.top})`);

      //Read the data
      d3.csv("profile.csv", function (error, csvdata) {

        if (error) {
          console.log(error);
        }
        //console.log(csvdata);
      },
      ).then(function (data) {
        // Add X axis
        const x = d3.scaleLinear()
          .domain([10, 70])
          .range([0, width]);
        svg.append("g")
          .attr("transform", `translate(0, ${height})`)
          .call(d3.axisBottom(x));

        // Add Y axis
        const y = d3.scaleLinear()
          .domain([0, 110])
          .range([height, 0]);
        svg.append("g")
          .call(d3.axisLeft(y));

        // Color scale: give me a specie name, I return a color
        const color = d3.scaleOrdinal()
          .domain(["Unknown_Male", "Black_Male", "Asian_Male", "White_Male", "Unknown", "White_Female", "Asian_Female", "Black_Female", "Unknown_Female"])
          .range(["#67001f", "#b2182b", "#ef8a62", "#fddbc7", "#ffffff", "#e0e0e0", "#e0e0e0", "#4d4d4d", "#1a1a1a"])

        // Highlight the specie that is hovered
        const highlight = function (event, d) {
          const selected_specie = d.race_sex
          console.log(selected_specie)
          d3.selectAll(".dot")
            .transition()
            .duration(200)
            .style("fill", "lightgrey")
            .attr("r", 3)

          d3.selectAll("." + selected_specie)
            .transition()
            .duration(200)
            .style("fill", color(selected_specie))
            .attr("r", 7)
        }

        // Highlight the specie that is hovered
        const doNotHighlight = function (event, d) {
          d3.selectAll(".dot")
            .transition()
            .duration(200)
            .style("fill", d => color(d.race_sex))
            .attr("r", 5)
        }

        // Add dots
        svg.append('g')
          .selectAll("dot")
          .data(data)
          .enter()
          .append("circle")
          .attr("class", function (d) { console.log(`HIHI${d.race_sex}`); return "dot " + d.race_sex})
          .attr("cx", function (d) { console.log(d.age); return x(d.age); })
          .attr("cy", function (d) { console.log(d.kill_injure); return y(d.kill_injure); })
          .attr("r", 5)
          .style("fill", function (d) { return color(d.race_sex); })
          // .on("mouseover", highlight)
          // .on("mouseleave", doNotHighlight)

    
      })
    })();
  }, [statesData.length]
  )

  return (
    <svg viewBox="0 0 960 600" className="usProfile" ref={ref}></svg>      //渲染了这个东西出来
  )
}

export default Profile
