import React, { useState, useEffect } from "react"
import { geoEqualEarth, geoPath } from "d3-geo"
import * as d3 from "d3"
import { feature } from "topojson-client"

const projection = geoEqualEarth()
  .scale(160)
  .translate([ 800 / 2, 450 / 2 ])

const Map = (props) => {
  const [statesData, setStatesData] = useState([]);

  const [statesTest, setStatesTest] = useState([]);

  useEffect(() => {
    (async () => {
      // const res = await fetch('https://willhaley.com/assets/united-states-map-react/states.json');
      const res = await fetch('./us.json')
      // const res1 = await fetch('./colorTest.json')
      const statesData = await res.json();
      // const statesData1 = await res1.json();
      // Set the statesData with the data received from fetch().
      setStatesData(statesData);
    })();
  }, []);

  useEffect(() => {
    (async () => {
      // const res = await fetch('https://willhaley.com/assets/united-states-map-react/states.json');
      const res = await fetch('./colorTest.json')
      // const res1 = await fetch('./colorTest.json')
      const statesTest = await res.json();
      // const statesData1 = await res1.json();
      // Set the statesData with the data received from fetch().
      setStatesTest(statesTest);
    })();
  }, []);



    // d3.select("svg").selectAll("path").style("opacity",statesTest.num)



  return (
     <svg viewBox="0 0 960 600" className="usMap">
      {statesData.map((stateData, statesTest, index) =>
        <path
          className="state"
          id={stateData.id}
          style={{cursor: "pointer", opacity: '0.7'}}
          key={stateData.id}
          // stroke="#fff"
          strokeWidth="0.5px"
          d={stateData.shape}
          // onMouseOver={(event) => {
          //   event.target.style.fill = 'red';
          // }}
          // onMouseOut={(event) => {
          //   event.target.style.fill = 'orange';
          // }}
        >
        </path>
      )}
    </svg>
  )
}

export default Map
