import React, { useState, useEffect, useLayoutEffect } from "react"
// import { geoEqualEarth, geoPath } from "d3-geo"
import { geoAlbers, geoPath, geoAlbersUsa } from "d3-geo"
import { useD3 } from './hooks/useD3';
import * as d3 from "d3"
import { feature } from "topojson-client"

// const projection = geoEqualEarth()
//   .scale(160)
//   .translate([ 800 / 2, 450 / 2 ])

function Map(props) {

  const ref = React.useRef(null);
  const [statesData, setStatesData] = useState([]);
  const [scaleData, setScaleData] = useState(props.scaleSend);
  const [eventsData, setEventsData] = useState([]);


  // let projection = geoAlbersUsa()
  // .scale(scaleData)
  // .translate([ 960 / 2, 600 / 2 ])

  // console.log(`OUT${scaleData}`)


  useEffect(() => {

    (async () => {
      const res = await fetch('./gz_2010_us_040_00_500k.json')
      const statesDataCombine = await res.json();
      const statesData = statesDataCombine.features
      setStatesData(statesData);
      setScaleData(props.scaleSend);

      const res1 = await fetch('./cases.geojson')
      const eventsDataCombine = await res1.json();
      const eventsData = eventsDataCombine.features;
      setEventsData(eventsData);

      const projection = geoAlbersUsa()
        .scale(scaleData)
        .translate([960 / 2, 600 / 2])

      const chartEl = d3.select(ref.current);
      chartEl.selectAll("*").remove();

      const chart = chartEl.append("g")

      const path = d3.geoPath()
        .projection(projection);

      const caseTip = d3.select(".case").append("div") 
        .attr("class", "caseTip")               
        .style("opacity", 0);

      console.log(scaleData);
      const myColor = d3.scaleLinear().domain([1, 10]).range(["white","#666666"])



      chart.selectAll("path")
        .data(statesData)
        .enter()
        .append("path")
        .attr("class", "state")
        .attr("id", function (d) {
          return d.properties.NAME
        })
        .attr("d", path)
        // .style("fill", "steelblue");
        // .style(
        //     "opacity", 
        //     function(d){
        //       for (let i = 0; i < props.data1.length ; i++){
        //         if (props.data1[i].name == d.properties.NAME) {
        //           // console.log(data1.data1[i].num)
        //           return props.data1[i].num + 0.05
        //         }
        //       } 
        //     })
        .style(
          "fill",
          function (d) {
            for (let i = 0; i < props.data2.length; i++) {
              if (props.data2[i].name == d.properties.NAME) {
                // console.log(myColor(props.data2[i]))
                console.log(`Here${myColor(props.data2[i].num * 10)}`)
                console.log(`Here${i}`)
                console.log(`Here${props.data2[i].num}`)
                return myColor(props.data2[i].num * 10)
              }
            }
          })
          .on("mousemove", function(d) {
            caseTip.html("<strong> Look, I'm bold !</strong> and now I'm not bold <br> and this is another line! and this is my data:")
                    // .style('top', d3.event.pageY - 12 + 'px')
                    // .style('left', d3.event.pageX + 25 + 'px')
                    .style("opacity", 1);
        })
        .on("mouseout", function(d) {
          caseTip.style("opacity", 0);
        })

      chart
        .selectAll(".points")
        .data(eventsData).enter()
        .append("circle")
        .attr("class", "case")
        .attr("r",
          function (d) {
            // console.log(d)
            return 4.0
            // }
          })
        .attr("transform",

          function (d) {
            // console.log(d)
            return "translate(" + projection(d.geometry.coordinates) + ")"
          })
    })();

  }, [statesData.length]
  )


  return (

    <svg viewBox="0 0 960 600" className="usMap Map" ref={ref}></svg>
  )
}

export default Map
