import React, { useState, useEffect } from "react"
import { geoEqualEarth, geoPath } from "d3-geo"
import { feature } from "topojson-client"

const projection = geoEqualEarth()
  .scale(160)
  .translate([ 800 / 2, 450 / 2 ])

const Map = (props) => {
  const [statesData, setStatesData] = useState([]);

  useEffect(() => {
    (async () => {
      // const res = await fetch('https://willhaley.com/assets/united-states-map-react/states.json');
      const res = await fetch('./us.json')
      const statesData = await res.json();
      // Set the statesData with the data received from fetch().
      setStatesData(statesData);
    })();
  }, []);

  return (

     <svg viewBox="0 0 960 600">
      {statesData.map((stateData, index) =>
        <path
          className="state"
          id={stateData.id}
          // style={{cursor: "pointer", fill: "orange"}}
          key={index}
          // stroke="#fff"
          strokeWidth="1px"
          d={stateData.shape}
          onMouseOver={(event) => {
            event.target.style.fill = 'red';
          }}
          onMouseOut={(event) => {
            event.target.style.fill = 'orange';
          }}
        >
        </path>
      )}
    </svg>
  )
}

export default Map
